<?class tip{	private $close='<button type="button" class="close" data-dismiss="alert" aria-hidden="true">&times;</button>';	public function success($txt='未知原因！',$close=0){		$result='<div class="alert alert-success">		'.($close!=0?$this->close:'').'		<strong>成功：</strong>'.$txt.'	  </div>';	  return $result;	}	public function info($txt='未知原因！',$close=0){		$result='<div class="alert alert-info">		'.($close!=0?$this->close:'').'		<strong>信息：</strong>'.$txt.'	  </div>';	  return $result;	}	public function warn($txt='未知原因！',$close=0){		$result='<div class="alert alert-warning">		'.($close!=0?$this->close:'').'		<strong>警告：</strong>'.$txt.'	  </div>';	  return $result;	}	public function danger($txt='未知原因！',$close=0){		$result='<div class="alert alert-danger">		'.($close!=0?$this->close:'').'		<strong>危险：</strong>'.$txt.'	  </div>';	  return $result;	}	public function get(){		}}?>